.git/hooks/fsmonitor-watchman.sample:
0001: #!/usr/bin/perl
0002: 
0003: use strict;
0004: use warnings;
0005: use IPC::Open2;
0006: 
0007: # An example hook script to integrate Watchman
0008: # (https://facebook.github.io/watchman/) with git to speed up detecting
0009: # new and modified files.
0010: #
0011: # The hook is passed a version (currently 2) and last update token
0012: # formatted as a string and outputs to stdout a new update token and
0013: # all files that have been modified since the update token. Paths must
0014: # be relative to the root of the working tree and separated by a single NUL.
0015: #
0016: # To enable this hook, rename this file to "query-watchman" and set
0017: # 'git config core.fsmonitor .git/hooks/query-watchman'
0018: #
0019: my ($version, $last_update_token) = @ARGV;
0020: 
0021: # Uncomment for debugging
0022: # print STDERR "$0 $version $last_update_token\n";
0023: 
0024: # Check the hook interface version
0025: if ($version ne 2) {
0026: 	die "Unsupported query-fsmonitor hook version '$version'.\n" .
0027: 	    "Falling back to scanning...\n";
0028: }
0029: 
0030: my $git_work_tree = get_working_dir();
0031: 
0032: my $retry = 1;
0033: 
0034: my $json_pkg;
0035: eval {
0036: 	require JSON::XS;
0037: 	$json_pkg = "JSON::XS";
0038: 	1;
0039: } or do {
0040: 	require JSON::PP;
0041: 	$json_pkg = "JSON::PP";
0042: };
0043: 
0044: launch_watchman();
0045: 
0046: sub launch_watchman {
0047: 	my $o = watchman_query();
0048: 	if (is_work_tree_watched($o)) {
0049: 		output_result($o->{clock}, @{$o->{files}});
0050: 	}
0051: }
0052: 
0053: sub output_result {
0054: 	my ($clockid, @files) = @_;
0055: 
0056: 	# Uncomment for debugging watchman output
0057: 	# open (my $fh, ">", ".git/watchman-output.out");
0058: 	# binmode $fh, ":utf8";
0059: 	# print $fh "$clockid\n@files\n";
0060: 	# close $fh;
0061: 
0062: 	binmode STDOUT, ":utf8";
0063: 	print $clockid;
0064: 	print "\0";
0065: 	local $, = "\0";
0066: 	print @files;
0067: }
0068: 
0069: sub watchman_clock {
0070: 	my $response = qx/watchman clock "$git_work_tree"/;
0071: 	die "Failed to get clock id on '$git_work_tree'.\n" .
0072: 		"Falling back to scanning...\n" if $? != 0;
0073: 
0074: 	return $json_pkg->new->utf8->decode($response);
0075: }
0076: 
0077: sub watchman_query {
0078: 	my $pid = open2(\*CHLD_OUT, \*CHLD_IN, 'watchman -j --no-pretty')
0079: 	or die "open2() failed: $!\n" .
0080: 	"Falling back to scanning...\n";
0081: 
0082: 	# In the query expression below we're asking for names of files that
0083: 	# changed since $last_update_token but not from the .git folder.
0084: 	#
0085: 	# To accomplish this, we're using the "since" generator to use the
0086: 	# recency index to select candidate nodes and "fields" to limit the
0087: 	# output to file names only. Then we're using the "expression" term to
0088: 	# further constrain the results.
0089: 	my $last_update_line = "";
0090: 	if (substr($last_update_token, 0, 1) eq "c") {
0091: 		$last_update_token = "\"$last_update_token\"";
0092: 		$last_update_line = qq[\n"since": $last_update_token,];
0093: 	}
0094: 	my $query = <<"	END";
0095: 		["query", "$git_work_tree", {$last_update_line
0096: 			"fields": ["name"],
0097: 			"expression": ["not", ["dirname", ".git"]]
0098: 		}]
0099: 	END
0100: 
0101: 	# Uncomment for debugging the watchman query
0102: 	# open (my $fh, ">", ".git/watchman-query.json");
0103: 	# print $fh $query;
0104: 	# close $fh;
0105: 
0106: 	print CHLD_IN $query;
0107: 	close CHLD_IN;
0108: 	my $response = do {local $/; <CHLD_OUT>};
0109: 
0110: 	# Uncomment for debugging the watch response
0111: 	# open ($fh, ">", ".git/watchman-response.json");
0112: 	# print $fh $response;
0113: 	# close $fh;
0114: 
0115: 	die "Watchman: command returned no output.\n" .
0116: 	"Falling back to scanning...\n" if $response eq "";
0117: 	die "Watchman: command returned invalid output: $response\n" .
0118: 	"Falling back to scanning...\n" unless $response =~ /^\{/;
0119: 
0120: 	return $json_pkg->new->utf8->decode($response);
0121: }
0122: 
0123: sub is_work_tree_watched {
0124: 	my ($output) = @_;
0125: 	my $error = $output->{error};
0126: 	if ($retry > 0 and $error and $error =~ m/unable to resolve root .* directory (.*) is not watched/) {
0127: 		$retry--;
0128: 		my $response = qx/watchman watch "$git_work_tree"/;
0129: 		die "Failed to make watchman watch '$git_work_tree'.\n" .
0130: 		    "Falling back to scanning...\n" if $? != 0;
0131: 		$output = $json_pkg->new->utf8->decode($response);
0132: 		$error = $output->{error};
0133: 		die "Watchman: $error.\n" .
0134: 		"Falling back to scanning...\n" if $error;
0135: 
0136: 		# Uncomment for debugging watchman output
0137: 		# open (my $fh, ">", ".git/watchman-output.out");
0138: 		# close $fh;
0139: 
0140: 		# Watchman will always return all files on the first query so
0141: 		# return the fast "everything is dirty" flag to git and do the
0142: 		# Watchman query just to get it over with now so we won't pay
0143: 		# the cost in git to look up each individual file.
0144: 		my $o = watchman_clock();
0145: 		$error = $output->{error};
0146: 
0147: 		die "Watchman: $error.\n" .
0148: 		"Falling back to scanning...\n" if $error;
0149: 
0150: 		output_result($o->{clock}, ("/"));
0151: 		$last_update_token = $o->{clock};
0152: 
0153: 		eval { launch_watchman() };
0154: 		return 0;
0155: 	}
0156: 
0157: 	die "Watchman: $error.\n" .
0158: 	"Falling back to scanning...\n" if $error;
0159: 
0160: 	return 1;
0161: }
0162: 
0163: sub get_working_dir {
0164: 	my $working_dir;
0165: 	if ($^O =~ 'msys' || $^O =~ 'cygwin') {
0166: 		$working_dir = Win32::GetCwd();
0167: 		$working_dir =~ tr/\\/\//;
0168: 	} else {
0169: 		require Cwd;
0170: 		$working_dir = Cwd::cwd();
0171: 	}
0172: 
0173: 	return $working_dir;
0174: }

